"use strict";
var $foreign = require("./foreign.js");
var Effect_Class = require("../Effect.Class/index.js");
var React_Basic = require("../React.Basic/index.js");
var toastError = function (dictMonadEffect) {
    return function (msg) {
        return Effect_Class.liftEffect(dictMonadEffect)(function () {
            return $foreign.toastError_(msg);
        });
    };
};
var toastContainer = React_Basic.element($foreign.toastContainer_)({});

// TODO: MonadToast?
var toast = function (dictMonadEffect) {
    return function (msg) {
        return Effect_Class.liftEffect(dictMonadEffect)(function () {
            return $foreign.toast_(msg);
        });
    };
};
module.exports = {
    toastContainer: toastContainer,
    toast: toast,
    toastError: toastError
};
